query getMembersStrategy($strategyId: String!) {
  memberStrategies(first: 1000, where: {strategy: $strategyId}) {
    activatedPoints
    member {
      memberCommunity {
        memberAddress
        isRegistered
      }
      stakes(
        first: 1000
        where: {proposal_: {strategy: $strategyId, proposalStatus_in: [1, 5]}}
      ) {
        amount
        proposal {
          proposalStatus
        }
      }
    }
    totalStakedPoints
    id
  }
}

