    token
    metadataHash
    metadata {
      title
      description
    }
    id
    poolId
    totalEffectiveActivePoints
    isEnabled
    maxCVSupply
    archived
    sybil {
      id
      type
    }
    memberActive(first: 1000) {
      id
    }
    config {
      id
      weight
      decay
      maxAmount
      maxRatio
      minThresholdPoints
      pointSystem
      proposalType
      allowlist
      superfluidToken
    }
    stream {
      id
      contractAddress
      contractType
      superfluidToken
      maxFlowRate
      superfluidGDA
      streamLastFlowRate
      totalMemberUnits
      proposalStreamIds
      createdAt
      updatedAt
    }
    registryCommunity {
      id
      councilSafe
      isValid
      garden {
        id
        symbol
        decimals
      }
      members(first: 1000) {
        memberAddress
      }
    }
    proposals(first: 1000, orderBy: convictionLast, orderDirection: desc) {
      id
      proposalNumber
      metadataHash
      metadata {
        title
        description
      }
      beneficiary
      streamingEscrow
      requestedAmount
      requestedToken
      proposalStatus
      stakedAmount
      convictionLast
      createdAt
      executedAt
      blockLast
      submitter
      strategy {
        id
        maxCVSupply
        totalEffectiveActivePoints
      }
      proposalStream {
        id
        proposal {
          id
        }
        escrow
        currentUnits
        currentFlowRate
        streamedUntilSnapshot
        lastSnapshotAt
        isStopped
        createdAt
        updatedAt
      }
    }
  }
  arbitrableConfigs(
    first: 1
    orderBy: version
    orderDirection: desc
    where: {strategy_: {poolId: $poolId}}
  ) {
    submitterCollateralAmount
    challengerCollateralAmount
    arbitrator
    defaultRuling
    defaultRulingTimeout
    tribunalSafe
